import React, { useState, Fragment } from "react";
import Places from "./Places";
import "./style.css";
import {
  useLoadScript,
  GoogleMap,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";

function App() {
  const [mapRef, setMapRef] = useState(null);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [markerMap, setMarkerMap] = useState({});
  const [center, setCenter] = useState({});
  const [clickedLatLng, setClickedLatLng] = useState(null);
  const [infoOpen, setInfoOpen] = useState(false);

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: "AIzaSyA73RWJvc7Pd6wMTVDYS5KPgbTuPPzOTNM",
  });

  const fitBounds = (map) => {
    const bounds = new window.google.maps.LatLngBounds();
    Places.map((place) => {
      bounds.extend(place.pos);
      return place.id;
    });
    map.fitBounds(bounds);
  };

  const loadHandler = (map) => {
    // console.log(map);
    setMapRef(map);
    fitBounds(map);
  };

  const markerLoadHandler = (marker, place) => {
    return setMarkerMap((prevState) => {
      return { ...prevState, [place.id]: marker };
    });
  };

use

  const renderMap = () => {
    return (
      <>
        <Fragment>
          <GoogleMap
            onLoad={loadHandler}
            onCenterChanged={() => setCenter(mapRef.getCenter().toJSON())}
            onClick={(e) => setClickedLatLng(e.latLng.toJSON())}
            center={center}
            mapContainerStyle={{
              height: "300px",
              width: "900px",
              margin: "auto",
            }}
          >
            {Places.map((place) => (
              <Marker
                key={place.id}
                position={place.pos}
                onLoad={(marker) => markerLoadHandler(marker, place)}
                onClick={(event) => markerClickHandler(event, place)}
                icon={{
                  icon: "https://gasdeverao.cocacola.com.br/assets/favicons/favicon-32x32.png",
                  // path: "M12.75 0l-2.25 2.25 2.25 2.25-5.25 6h-5.25l4.125 4.125-6.375 8.452v0.923h0.923l8.452-6.375 4.125 4.125v-5.25l6-5.25 2.25 2.25 2.25-2.25-11.25-11.25zM10.5 12.75l-1.5-1.5 5.25-5.25 1.5 1.5-5.25 5.25z",

                  fillColor: "#ff0000",
                  fillOpacity: 1,
                }}
              />
            ))}

            {infoOpen && selectedPlace && (
              <InfoWindow
                anchor={markerMap[selectedPlace.id]}
                onCloseClick={() => setInfoOpen(false)}
              >
                <div className="card_container">
                  <div className="card_img">
                    <img src={selectedPlace.image} alt="house img" />
                  </div>
                  <div className="card_dites">
                    <a
                      style={{ textDecoration: "none", fontWeight: "900" }}
                      href={selectedPlace.link}
                    >
                      <b>{selectedPlace.title}</b>
                    </a>
                    <div>{selectedPlace.price}</div>
                    <div>{selectedPlace.area}</div>
                    <div
                      style={{ textTransform: "uppercase", fontWeight: "400" }}
                    >
                      {selectedPlace.description}
                    </div>
                  </div>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </Fragment>
        <div className="house_container">
          {Places.map((home) => (
            <div
              className="house"
              onMouseEnter={(e) => markerClickHandler(e, home)}
              // onClick={(marker) => markerLoadHandler(marker, home)}
            >
              <img className="house_img" src={home.image} alt="house img" />
              <div className="house_details">
                <a
                  style={{ textDecoration: "none", fontWeight: "900" }}
                  href={home.link}
                >
                  <b>{home.title}</b>
                </a>
                <div>{home.price}</div>
                <div>{home.area}</div>
                <div style={{ textTransform: "uppercase", fontWeight: "400" }}>
                  {home.description}
                </div>
              </div>
            </div>
          ))}
        </div>
      </>
    );
  };
  return isLoaded ? renderMap() : null;
}

export default App;
